import{V as a}from"./chunks/theme.B-f5TZCO.js";import{c as t,I as r,k as i,a2 as o,o as s}from"./chunks/framework.Dzy1sSWx.js";const n=o('<h1 id="what-is-modularity" tabindex="-1">What is Modularity <a class="header-anchor" href="#what-is-modularity" aria-label="Permalink to &quot;What is Modularity&quot;">​</a></h1><p><a href="https://github.com/unusualify/modularity" target="_blank" rel="noreferrer">Unusualify/Modularity</a> is a Laravel and Vuetify.js powered, developer tool that aims to improve developer experience on conducting full stack development process. On Laravel side, Modularity manages your large scale projects using modules, where a module similar to a single Laravel project, having some views, controllers or models. With the abilities of Vuetify.js, Modularity presents various of dynamic, configurable UI components to auto-construct a CRM for your project.</p><h2 id="developer-experience" tabindex="-1">Developer Experience <a class="header-anchor" href="#developer-experience" aria-label="Permalink to &quot;Developer Experience&quot;">​</a></h2><p>Modularity aims to provide a greate Developer Experince when working on full-stack development process with:</p><ul><li>Presenting various custom artisan commands that undergoes file generation</li><li>Generating CRUD pages and forms based on the defined model using ability of <a href="https://vuetifyjs.com/en/" target="_blank" rel="noreferrer">Vuetify.js</a></li><li>Simplistic configuration or customization on the crm panel UI through config files</li><li>Simplistic configuration of CRUD forms through config files</li></ul><h2 id="organised-project-structure" tabindex="-1">Organised Project Structure <a class="header-anchor" href="#organised-project-structure" aria-label="Permalink to &quot;Organised Project Structure&quot;">​</a></h2><p>A module in Modularity project is like a Laravel Project, it has some views, controllers or models. ![projectStructure]</p><h2 id="dynamic-configurable-panel-ui" tabindex="-1">Dynamic &amp; Configurable Panel UI <a class="header-anchor" href="#dynamic-configurable-panel-ui" aria-label="Permalink to &quot;Dynamic &amp; Configurable Panel UI&quot;">​</a></h2><h2 id="used-packages" tabindex="-1">Used Packages <a class="header-anchor" href="#used-packages" aria-label="Permalink to &quot;Used Packages&quot;">​</a></h2><h2 id="for-questions-and-issues" tabindex="-1">For Questions and Issues <a class="header-anchor" href="#for-questions-and-issues" aria-label="Permalink to &quot;For Questions and Issues&quot;">​</a></h2><h2 id="future-work" tabindex="-1">Future Work <a class="header-anchor" href="#future-work" aria-label="Permalink to &quot;Future Work&quot;">​</a></h2><h2 id="main-contributers" tabindex="-1">Main Contributers <a class="header-anchor" href="#main-contributers" aria-label="Permalink to &quot;Main Contributers&quot;">​</a></h2>',12),p=JSON.parse('{"title":"What is Modularity","description":"","frontmatter":{},"headers":[],"relativePath":"user-guide/get-started/what-is-modularity.md","filePath":"user-guide/get-started/what-is-modularity.md","lastUpdated":null}'),l={name:"user-guide/get-started/what-is-modularity.md"},f=Object.assign(l,{setup(u){const e=[{avatar:"https://avatars.githubusercontent.com/u/80110747",name:"Ilker CIBLAK",title:"Full Stack Developer",links:[{icon:"github",link:"https://github.com/ilkerciblak"},{icon:"twitter",link:"https://twitter.com/ilker_exe"}]}];return(c,d)=>(s(),t("div",null,[n,r(i(a),{size:"small",members:e})]))}});export{p as __pageData,f as default};
