export default {
  name: 'ue-list-element',
  props: {
    items: {
      type: Array,
      required: true
    },
    level: {
      type: Number,
      default: 0
    }
  },
  data: function () {
    return {
      activeBackgroundColorLevels: ['#97ffff',
      // light blue
      '#7cffb9' // light green
      ]
    };
  },

  computed: {
    activeIndex: {
      get() {
        // __log(
        //     'getter selectIndex',
        //     this.items,
        //     this.items.find(item => item.is_active == 1 ),
        //     this.items.findIndex(item => item.is_active == 1 )
        // )
        return this.items.findIndex(item => item.is_active == 1);
      },
      set(value) {
        // __log('setter selectIndex', value)
      }
    },
    style() {
      // __log(
      //     'listGroup Style',
      //     this.$root.miniStatus,
      //     this.$root.isMini
      // )
      return {
        marginLeft: (!this.$root.miniStatus || !this.$root.isMini ? this.level * 20 : 0) + 'px'
      };
    }
  },
  created() {},
  methods: {
    isSubgroup(item) {
      return !!item.items;
    },
    isLink(item) {
      return !item.items && !!item.link;
    },
    isEvent(item) {
      return !!item.attr;
    },
    isHeader(item) {
      return !item.link && !item.items;
    }
  }
};
